import Layout from '../components/Layout.js';
import Results from '../components/Results.js';
import React, { useState } from "react";
import API from "../utils/API.js";

export default function Saved(){

    const [results, setResults] = useState([{ id: '08b2Nzn-mRUC',
    volumeInfo:
     { canonicalVolumeLink:
        'https://books.google.com/books/about/Lord_of_Light.html?hl=&id=08b2Nzn-mRUC',
       title: 'Lord of Light',
       authors: [ 'Roger Zelazny' ],
       imageLinks:
        { thumbnail:
           'http://books.google.com/books/content?id=08b2Nzn-mRUC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api' },
       description:
        'Earth is long since dead. On a colony planet, a band of men has gained control of technology, made themselves immortal, and now rules their world as the gods of the Hindu pantheon. Only one dares oppose them: he who was once Siddhartha and is now Mahasamatman. Binder of Demons. Lord of Light.' }}])
    const [type, setType] = useState("Delete");

    const [update, setUpdate] = useState(true);




    const updateList = async() => {
        console.log("Got to update list thing")
        if (update === true){
        await setResults(API.getBooks)
        setUpdate(false);
        }

    }




    return (
    
        <>
        {updateList}

        <Layout>

    
        
            <div className="container resultsBox">

                <h4 className="subHeader">Results</h4>

                <Results results = {results} 
                type = {type}
                setUpdate = {setUpdate}
                updateList = {updateList}
                
                />


            </div>



        </Layout>


    
        </>
    
    )

}
